  let autor, texto;

  useEffect(()=>{
    const fetchData = async() =>{
        if(!loaded){
            const result = await axios.post('http://localhost:3002/new-tweet',{
              author: autor,
              text: texto
            })
            if(result.data){
              setTweets(result.data.tweets)
              setLoaded(true)
            }
        }
    }
    fetchData()
  })
  return (
    <div>
      <h3 className="center">Escribe un nuevo tweet </h3>
    </div>
  );

}

