import React,{useEffect, useState} from "react";
import axios from 'axios'

function New_Twitt(){
  
  const [loaded, setLoaded] = useState(false);

  let autor = "";
  let texto = "";

  useEffect(()=>{
    const fetchData = async() =>{
      if(!loaded){
        try {
          const result = await axios.post('http://localhost:3002/new-tweet',{
            author: autor,
            text: texto
          })
          console.log(result)
          setLoaded(true)
        } catch (error) {
          console.log(error)
        }
      }
    }
    fetchData()
  })

  const handleChange = e => {
    const text = e.target.value;

    this.setState(() => ({
      text
    }));
  };

  return (
    <div>
      <h3 className="center">Escribe un nuevo tweet</h3>

      <form className="new-twitt">
        <textarea
            placeholder="New tweet"
            maxLength={140}
            value={texto}
            onChange={this.handleChange}
          />

          {/* button is disabled if it's an empty string */}
          <button className="btn" type="submit" disabled={texto === ""} href="/">
            Submit
          </button>

      </form>
    </div>
  );

}

export default New_Twitt;
