import React,{useEffect, useState} from "react";
import axios from 'axios'

function New_Twitt(){
  
  const [loaded, setLoaded] = useState(false);

  const [datos, setDatos] = useState({
    autor: '',
    texto: ''
  })

  const handleChange = (event) => {
    setDatos({
        ...datos,
        [event.target.value] : event.target.value
    })
  }



  useEffect(()=>{
    const fetchData = async() =>{
      if(!loaded){
        try {
          const result = await axios.post('http://localhost:3002/new-tweet',{
            author: datos.autor,
            text: datos.texto
          })
          console.log(result)
          setLoaded(true)
        } catch (error) {
          console.log(error)
        }
      }
    }
    fetchData()
  })

  return (
    <div>
      <h3 className="center">Escribe un nuevo tweet</h3>

      <form className="new-twitt" onSubmit={useEffect} action="/">
        <textarea
            placeholder="New tweet"
            maxLength={140}
            value={datos.texto}
            onChange={handleChange}
          />

          {/* button is disabled if it's an empty string */}
          <button className="btn" type="submit" disabled={texto === ""}>
            Submit
          </button>

      </form>
    </div>
  );

}

export default New_Twitt;
